import { useState } from "react";

function InvestmentForm(){
    const FormHandler=(event)=>{
            event.preventDefault();
    }
    const[useCurrent,setCurrent]=useState("current");
    const[useyear,setyear]=useState("year");
    const[useintrest,setintrest]=useState("intrest");
    const[useDuration,setDuration]=useState("Duration");

     const currentHandler=(event)=>{
        let store=event.target.value;
        setCurrent(store);
        console.log(useCurrent);
     }

    return(
        <div>
                <form className="form" onSubmit={FormHandler}>
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input type="number" id="current-savings" onChange={currentHandler}/>
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution" />
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number" id="expected-return" />
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration" />
          </p>
        </div>
        <p className="actions">
          <button type="reset" className="buttonAlt">
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>
        </div>
    )
}
export default InvestmentForm;
