import { useState } from "react";

function InvestmentForm(){
    const FormHandler=(event)=>{
            event.preventDefault();
           const InvestmentDetails={
            current_savings:useCurrent,
            yearly_savings:useyear,
            intrest:useintrest,
            duration:useDuration
           }
          // console.log(InvestmentDetails);
    }
    const[useCurrent,setCurrent]=useState();
    const[useyear,setyear]=useState("year");
    const[useintrest,setintrest]=useState("intrest");
    const[useDuration,setDuration]=useState("Duration");

     const currentHandler=(event)=>{
        setCurrent(event.target.value);
     }
     const YearlyHandler=(event)=>{
      setyear(event.target.value);
   }
   const InterestHandler=(event)=>{
    setintrest(event.target.value);
 }
 const DurationHandler=(event)=>{
  setDuration(event.target.value);
}
    return(
     
        <div>
                <form className="form" onSubmit={FormHandler}>
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input type="number" id="current-savings" onChange={currentHandler}/>
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution" onChange={YearlyHandler} />
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number" id="expected-return"  onChange={InterestHandler}/>
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration"  onChange={DurationHandler}/>
          </p>
        </div>
        <p className="actions">
          <button type="reset" className="buttonAlt">
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>
        </div>
        
    )
}
export default InvestmentForm;
